```typescript
import { Controller, Post, Body } from '@nestjs/common';
import Stripe from 'stripe';

@Controller('stripe')
export class StripeController {
  private stripe: Stripe;

  constructor() {
    // Initialize Stripe with your API key
    this.stripe = new Stripe('YOUR_STRIPE_API_KEY', {
      apiVersion: '2020-08-27',
    });
  }

  @Post('payment-intent')
  async createPaymentIntent(@Body() data: { customerId: string, amount: number }): Promise<Stripe.PaymentIntent> {
    // Create a new payment intent for the given customer and amount
    const paymentIntent = await this.stripe.paymentIntents.create({
      amount: data.amount,
      currency: 'usd',
      customer: data.customerId,
      payment_method_types: ['card'],
      setup_future_usage: 'off_session',
    });

    return paymentIntent;
  }

  @Post('setup-intent')
  async createSetupIntent(@Body() data: { customerId: string }): Promise<Stripe.SetupIntent> {
    // Create a new setup intent for the given customer
    const setupIntent = await this.stripe.setupIntents.create({
      customer: data.customerId,
      usage: 'off_session',
    });

    return setupIntent;
  }

  @Post('add-card')
  async addCard(@Body() data: { setupIntentId: string, paymentMethodId: string }): Promise<Stripe.PaymentMethod> {
    // Get the setup intent and payment method from the provided IDs
    const setupIntent = await this.stripe.setupIntents.retrieve(data.setupIntentId);
    const paymentMethod = await this.stripe.paymentMethods.retrieve(data.paymentMethodId);

    // Attach the payment method to the customer using the setup intent
    await this.stripe.setupIntents.confirm(data.setupIntentId, {
      payment_method: data.paymentMethodId,
    });

    return paymentMethod;
  }

  @Post('charge-card')
  async chargeCard(@Body() data: { paymentIntentId: string, paymentMethodId: string }): Promise<Stripe.PaymentIntent> {
    // Get the payment intent and payment method from the provided IDs
    const paymentIntent = await this.stripe.paymentIntents.retrieve(data.paymentIntentId);
    const paymentMethod = await this.stripe.paymentMethods.retrieve(data.paymentMethodId);

    // Attach the payment method to the customer if it hasn't already been attached
    if (!paymentMethod.customer) {
      await this.stripe.paymentMethods.attach(data.paymentMethodId, {
        customer: paymentIntent.customer,
      });
    }

    // Confirm the payment intent with the payment method to charge the card
    const result = await this.stripe.paymentIntents.confirm(data.paymentIntentId, {
      payment_method: data.paymentMethodId,
    });

    return result;
  }
}
```
