class CropSimulator:
    def __init__(self, crop_type, climate_data, soil_data):
        self.crop_type = crop_type
        self.climate_data = climate_data
        self.soil_data = soil_data
        self.days_to_maturity = self.get_days_to_maturity()
        self.growth_stage = 0
        self.daily_gdd = self.get_daily_gdd()

    def get_days_to_maturity(self):
        # calculate days to maturity based on crop type
        if self.crop_type == "Corn":
            days_to_maturity = 100
        elif self.crop_type == "Wheat":
            days_to_maturity = 120
        elif self.crop_type == "Soybeans":
            days_to_maturity = 80
        else:
            days_to_maturity = 0
            print("Error: Invalid crop type specified.")
        return days_to_maturity

    def get_daily_gdd(self):
        # calculate daily growing degree days (GDD) based on climate data
        daily_gdd = []
        for temp in self.climate_data:
            gdd = (temp - 10) / 2
            if gdd < 0:
                gdd = 0
            daily_gdd.append(gdd)
        return daily_gdd

    def simulate_growth(self, days):
        # simulate crop growth over a specified number of days
        for i in range(days):
            if self.growth_stage == 0 and self.daily_gdd[i] >= 10:
                self.growth_stage = 1
                print("Crop has emerged.")
            elif self.growth_stage == 1 and self.daily_gdd[i] >= 15:
                self.growth_stage = 2
                print("Crop is in vegetative growth stage.")
            elif self.growth_stage == 2 and self.daily_gdd[i] >= 20:
                self.growth_stage = 3
                print("Crop is in reproductive growth stage.")
            elif self.growth_stage == 3 and i >= self.days_to_maturity:
                self.growth_stage = 4
                print("Crop is ready for harvest.")
                break
            else:
                print("Crop is still growing.")
