                continue; // Skip rows with an empty object type
            }

            switch (objectType.toLowerCase()) {
                case "campaign":
                    ModelActionWrapper<Campaign> campaignWrapper = parseCampaignWrapper(row, columnMapping);
                    campaignWrappers.add(campaignWrapper);
                    break;
                case "adgroup":
                    ModelActionWrapper<AdGroup> adGroupWrapper = parseAdGroupWrapper(row, columnMapping);
                    adGroupWrappers.add(adGroupWrapper);
